def format_output(title, content):
    """Helper function to format search output in a more readable way"""
    print(f"\n{'=' * 80}")
    print(f"=== {title} ===")
    print(f"{'=' * 80}")

    if isinstance(content, list):
        for i, item in enumerate(content[:5], 1):
            print(f"\n[Result {i}]")
            if isinstance(item, dict):
                for key, value in item.items():
                    if key == "code":
                        print(f"\n{key.capitalize()}:")
                        print("```")
                        print(value)
                        print("```")
                    else:
                        print(f"{key.capitalize()}: {value}")
            else:
                print(item)

        if len(content) > 5:
            print(f"\n... and {len(content) - 5} more results")
    else:
        print(content)

def setup_openai_embed_fn():
    """Try to set up an OpenAI embedding function if the API key is available"""
    try:
        import openai
        from openai import OpenAI

        # Check if API key is available in environment
        api_key = os.environ.get("OPENAI_API_KEY")
        if not api_key:
            return None

        # Create OpenAI client
        client = OpenAI()

        # Define embedding function
        def embed_fn(text):
            try:
                response = client.embeddings.create(
                    input=text,
                    model="text-embedding-ada-002"
                )
                return response.data[0].embedding
            except Exception as e:
                print(f"Error generating embeddings: {e}")
                return None

        return embed_fn
    except ImportError:
        return None

